using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.IO;
using System.Net;
using System.Net.Sockets;

namespace PacketSender
{
    public class Program
    {

        static String prefix = "[Packet Sender]: ";
        public static void Main(string[] args)
        {
            Console.ForegroundColor = ConsoleColor.White;
            Console.Title = "PacketSender v1.0 (C#)";

            Console.WriteLine(prefix + "Packet Sender v1.0 by Yochran has loaded.\n");
            Console.Write(prefix + "Enter the IP address you want to send packets to: ");
            Console.ForegroundColor = ConsoleColor.Green;
            String ip = Console.ReadLine();

            Console.ForegroundColor = ConsoleColor.White;
            Console.Write("\n" + prefix + "Enter the port you want to send the packets on: ");
            Console.ForegroundColor = ConsoleColor.Green;
            int port = 0;
            
            try
            {
                port = int.Parse(Console.ReadLine());
            } catch (SystemException e)
            {
                Console.WriteLine(prefix + "You wrote an invalid value. (Must Be Integer)");
            }

            Console.ForegroundColor = ConsoleColor.White;
            Console.Write("\n" + prefix + "How many packets per loop do you want to send: ");
            Console.ForegroundColor = ConsoleColor.Green;
            double packets = 0;
            try
            {
                packets = double.Parse(Console.ReadLine());
            }
            catch (SystemException e)
            {
                Console.WriteLine(prefix + "You wrote an invalid value. (Must Be Double, 15 digits max.)");
            }

            Console.ForegroundColor = ConsoleColor.White;

            Console.WriteLine("\n" + prefix + $"Sending {packets} packets to {ip} on {port} port...\n");

            sendPackets(ip, port, packets);
        }

        public static void sendPackets(String ip, int port, double packets)
        {
            String[] stringList;

            String one = "";
            String two = "";
            String three = "";
            String four = "";
            String five = "";
            String six = "";
            String seven = "";
            String eight = "";
            String nine = "";
            String ten = "";
            String eleven = "";
            String twelve = "";
            String thirteen = "";
            String fourteen = "";
            String fifteen = "";
            String sixteen = "";
            String seventeen = "";
            String eighteen = "";
            String nineteen = "";
            String twenty = "";

            String list = one + two + three + four + five + six + seven + eight + nine + ten + eleven + twelve + thirteen + fourteen + fifteen + sixteen + seventeen + eighteen + nineteen + twenty;

            try
            {
                while (true)
                {
                    for (int i = 0; i < packets; i++)
                    {
                        byte[] packetData = System.Text.ASCIIEncoding.ASCII.GetBytes(list);
                        IPEndPoint location = new IPEndPoint(IPAddress.Parse(ip), port);
                        Socket client = new Socket(AddressFamily.InterNetwork, SocketType.Dgram, ProtocolType.Udp);
                        client.SendTimeout = 1;

                        client.SendTo(packetData, location);
                    }
                }
            } catch (SystemException e)
            {
                Console.WriteLine("\n" + prefix + "You sent too many packets.");
                Console.ReadLine();
            }

            Console.WriteLine(prefix + $"Sent {packets} packets to {ip} on {port}.");
        }
    }
}
